properties.script:
var newForms = new java.util.ArrayList()
var dbConn = DatabaseConnectionFactory.createDatabaseConnection('com.mysql.jdbc.Driver', globalMap.get("ODK_DATABASE_URL"), globalMap.get("ODK_DATABASE_USER"), globalMap.get("ODK_DATABASE_PASSWORD"));

var locationResult = dbConn.executeCachedQuery("SELECT "+ globalMap.get("LOCATION_COLUMNS") +" FROM " + globalMap.get("LOCATION_TABLE") + " WHERE processed_by_mirth IS NULL ORDER BY COLLECTION_DATE_TIME");

while (locationResult.next()) {
  // create the locationf
  var locationXML = resultSetToXml(locationResult, "<locationForm event='locationForm' table-name='" + globalMap.get("LOCATION_TABLE") + "'/>");
  newForms.add(locationXML.toString());
}

// emit new individuals
var individualResult = dbConn.executeCachedQuery("SELECT "+ globalMap.get("INDIVIDUAL_COLUMNS") +" FROM " + globalMap.get("INDIVIDUAL_TABLE") + " WHERE processed_by_mirth IS NULL ORDER BY COLLECTION_DATE_TIME");
while (individualResult.next()) {
  // create the individual herself
  var individualXML = resultSetToXml(individualResult, "<individual event='individual' table-name='" + globalMap.get("INDIVIDUAL_TABLE") + "'/>");
  newForms.add(individualXML.toString());
}

// emit new visits
var visitResult = dbConn.executeCachedQuery("SELECT "+ globalMap.get("VISIT_COLUMNS") +" FROM " + globalMap.get("VISIT_TABLE") + " WHERE processed_by_mirth IS NULL");
while (visitResult.next()) {
  // create the visit
  var visitXML = resultSetToXml(visitResult, "<visit event='visit' table-name='" + globalMap.get("VISIT_TABLE") + "'/>");
  newForms.add(visitXML.toString());

}

// emit new pregnancy observations
var pregObservationResult = dbConn.executeCachedQuery("SELECT "+ globalMap.get("PREGNANCY_OBSERVATION_COLUMNS") +" FROM " + globalMap.get("PREGNANCY_OBSERVATION_TABLE") + " WHERE processed_by_mirth IS NULL");
while (pregObservationResult.next()) {
  // create the death
  var pregObservationXml = resultSetToXml(pregObservationResult, "<pregnancyobservation event='pregnancyobservation' table-name='" + globalMap.get("PREGNANCY_OBSERVATION_TABLE") + "'/>");
  newForms.add(pregObservationXml.toString());

}


dbConn.close();
return newForms;

