properties.script:
var newForms = new java.util.ArrayList()
var dbConn = DatabaseConnectionFactory.createDatabaseConnection('com.mysql.jdbc.Driver', globalMap.get("ODK_DATABASE_URL"), globalMap.get("ODK_DATABASE_USER"), globalMap.get("ODK_DATABASE_PASSWORD"));

try {
  // Query ODK tables in specific sequence and emit Mirth events
  var odkTableSequence = globalMap.get("ODK_TABLE_SEQUENCE");
  var nTables = odkTableSequence.length;
  var noFormsFoundYet = true;
  for (var i = 0; i < nTables && noFormsFoundYet; i++) {

    // the name of the next ODK table to query
    var tableName = odkTableSequence[i];

    // the name of the events to emit for this table
    var eventName = globalMap.get(tableName);

    // how many forms to process at a time
    var formLimit = globalMap.get("ODK_FORM_LIMIT");

    // get one batch of forms from ODK
    var queryResults = dbConn.executeCachedQuery("SELECT * FROM " + tableName + " WHERE PROCESSED_BY_MIRTH IS NULL ORDER BY COLLECTION_DATE_TIME LIMIT " + formLimit);

    while (queryResults.next()) {
      // emit one Mirth event per row in the result set
      var resultXML = resultSetToXml(queryResults, "<" + eventName + " event='" + eventName + "' table-name='" + tableName + "'/>");
      newForms.add(resultXML.toString());

      // mark each row as processed
      var uri = resultXML["_uri"].toString();
      var result = dbConn.executeUpdate("UPDATE " + tableName + " SET PROCESSED_BY_MIRTH = true WHERE _URI = '" + uri + "'");

      // processed at least one form, so exit
      noFormsFoundYet = false;
    }
  }
} finally {
  dbConn.close();
}

return newForms;

channel.deployScript:
// This script executes once when the channel is deployed
// You only have access to the globalMap and globalChannelMap here to persist data
return;

channel.undeployScript:
// This script executes once when the channel is undeployed
// You only have access to the globalMap and globalChannelMap here to persist data
return;
