// This script executes once when all channels start up from a redeploy
// You only have access to the globalMap here to persist data

// odk credentials
globalMap.put('ODK_DATABASE_URL','jdbc:mysql://localhost/odk_prod');
globalMap.put('ODK_DATABASE_USER','mirth_odk');
globalMap.put('ODK_DATABASE_PASSWORD','g0m1rthg0!');

// openhds credentials
globalMap.put('OPENHDS_PATH','localhost:8080/openhds');
globalMap.put('OPENHDS_USER','openhds');
globalMap.put('OPENHDS_PASSWORD','openhds');

// sequence of odk tables that Mirth should query
globalMap.put('ODK_TABLE_SEQUENCE', [
				'LOCATION_CORE',
				'INDIVIDUAL_CORE',
			 	'VISIT_CORE',
				'PREGNANCY_OBSERVATION_CORE', 
				'PREGNANCY_OUTCOME_CORE',
				'PREGNANCY_OUTCOME_OUTCOMES', 
				'OUT_MIGRATION_CORE', 
				'IN_MIGRATION_CORE', 
				'DEATH_CORE'])

// map odk table names to form events that Mirth should emit
globalMap.put('ODK_TABLE_EVENT', {
	LOCATION_CORE: 'locationForm',
	INDIVIDUAL_CORE: 'individualForm',
	VISIT_CORE: 'visitForm',
	PREGNANCY_OBSERVATION_CORE: 'pregnancyObservationForm',
	PREGNANCY_OUTCOME_CORE: 'pregnancyOutcomeCoreForm',
	PREGNANCY_OUTCOME_OUTCOMES: 'pregnancyOutcomeOutcomesForm',
	OUT_MIGRATION_CORE: 'outMigrationForm',
	IN_MIGRATION_CORE: 'inMigrationForm',
	DEATH_CORE: 'deathForm'
});

// how many ODK forms Mirth should try to process at a time
globalMap.put('ODK_FORM_LIMIT', '300');

return;

// Modify the message variable below to pre process data
// This script applies across all channels
return message;

// This script executes once after a message has been processed
// This script applies across all channels
